// Views/Blogs/Blogs.js
import { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom'
import axios from 'axios'
import Styles from './Blogs.module.css'

export function Blogs() {
    const [blogs, setBlogs] = useState([])
    const [searchTerm, setSearchTerm] = useState('')
    const navigate = useNavigate()

    useEffect(() => {
        fetchBlogs()
    }, [])

    const fetchBlogs = async () => {
        try {
            const token = localStorage.getItem('token')
            const response = await axios.get(`${process.env.REACT_APP_SERVER_URL}/api/blog`, {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            })
            setBlogs(response.data)
        } catch (error) {
            console.error('Error fetching blogs:', error)
        }
    }

    const filteredBlogs = blogs.filter(blog =>
        blog.title.toLowerCase().includes(searchTerm.toLowerCase())
    )

    const handleCreateNew = () => {
        navigate('/create-blog')  // Navigate to the create blog page
    }

    return (
        <div className={Styles.blogsPageContainer}>
            <div className={Styles.leftPanel}>
                <h1>Blogs</h1>
                {filteredBlogs.map(blog => (
                    <div key={blog._id} className={Styles.blogItem}>
                        <h2>{blog.title}</h2>
                        <p>{blog.description}</p>
                    </div>
                ))}
            </div>
            <div className={Styles.rightPanel}>
                <input
                    type="text"
                    placeholder="Search blogs..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className={Styles.searchBar}
                />
                <button onClick={handleCreateNew} className={Styles.createButton}>
                    Create New Blog
                </button>
            </div>
        </div>
    )
}
