import { useEffect, useState } from 'react'
import Styles from './TODO.module.css'
import axios from 'axios'

export function AUTH(props) {

    const [newBlog, setNewBlog] = useState('')
    const [blogData, setBlogData] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchTodo = async () => {
            const apiData = await getBlog()
            setBlogData(apiData);
            setLoading(false)
        }
        fetchTodo();
    }, [])

    const getBlog = async () => {
        const options = {
            method: "GET",
            url: `${process.env.REACT_APP_SERVER_URL}/api/blog`,
            headers: {
                accept: "application/json"
            }
        }
        try {
            const response = await axios.request(options)
            return response.data;
        } catch (error) {
            console.log(error);
            return [];
        }
    }

    const addBlog = () => {
        const options = {
            method: "POST",
            url: `${process.env.REACT_APP_SERVER_URL}/api/blog`,
            headers: {
                accept: "application/json"
            },
            data: {
                title: newBlog
            }
        }
        axios
        .request(options)
        .then(response => {
            console.log(response.data);
            setBlogData(prevData => [...prevData, response.data])
        })
        .catch(error => {
            console.log(error)
        })
    }

    const deleteBlog = (id) => {
        const options = {
            method: "DELETE",
            url: `${process.env.REACT_APP_SERVER_URL}/api/blog/${id}`,
            headers: {
                accept: "application/json"
            }

        }
        axios
        .request(options)
        .then(response => {
            console.log(response.data);
            setBlogData(prevData => prevData.filter(blog => blog._id !== id))
        })
        .catch(error => {
            console.log(error)
        })
    };

    const updateBlog = (id) => {
        const blogToUpdate = blogData.find(blog => blog._id === id)
        const options = {
            method: "PATCH",
            url: `${process.env.REACT_APP_SERVER_URL}/api/blog/${id}`,
            headers: {
                accept: "application/json"
            },
            data: {
                ...blogToUpdate,
                done: !blogToUpdate.done
            }
        }
        axios
        .request(options)
        .then(response => {
            console.log(response.data);
            setBlogData (prevData => prevData.map(blog => blog._id === id ? response.data : blog))
        })
        .catch(error => {
            console.log(error)
        })
    };

    return (
        <div className={Styles.ancestorContainer}>
            <div className={Styles.headerContainer}>
                <h1>
                    Blogs
                </h1>
                <span>
                    <input
                        className={Styles.todoInput}
                        type='text'
                        name='New Blog'
                        value={newBlog}
                        onChange={(event) => {
                            setNewBlog(event.target.value)
                        }}
                    />
                    <button
                        id='addButton'
                        name='add'
                        className={Styles.addButton}
                        onClick={() => {
                            addBlog()
                            setNewBlog('')
                        }}
                    >
                        + New Blog
                    </button>
                </span>
            </div>
            <div id='blogContainer' className={Styles.todoContainer}>
                {loading ? (
                    <p style={{ color: 'white' }}>Loading...</p>
                ) : (
                    blogData?.length > 0 ? (
                        blogData?.map((entry, index) => (
                            <div key={entry._id} className={Styles.todo}>
                                <span className={Styles.infoContainer}>
                                    <input
                                        type='checkbox'
                                        checked={entry.done}
                                        onChange={() => {
                                            updateBlog(entry._id);
                                        }}
                                    />
                                    {entry.title}
                                </span>
                                <span
                                    style={{ cursor: 'pointer' }}
                                    onClick={() => {
                                        deleteBlog(entry._id);
                                    }}
                                >
                                    Delete
                                </span>
                            </div>
                        ))
                    ) : (
                        <p className={Styles.noTodoMessage}>No tasks available. Please add a new task.</p>
                    )
                )}
            </div>
        </div>
    )
}
