import { useState } from 'react'
import Styles from './AUTH.module.css'
import axios from 'axios'

export function AUTH() {
    const [username, setUsername] = useState('')
    const [password, setPassword] = useState('')
    const [email, setEmail] = useState('')
    const [isLogin, setIsLogin] = useState(true)
    const [message, setMessage] = useState('')

    const handleSubmit = async (e) => {
        e.preventDefault()
        const url = isLogin 
            ? `${process.env}/login`
            : `http://localhost:8000/register`
        
        console.log("Sending request to: ", url);
        const data = isLogin 
            ? { username, password }
            : { email, username, password }

        try {
            const response = await axios.post(url, data, {
                headers: { accept: "application/json" }
            })
            console.log(response.data)
            setMessage(isLogin ? "Login successful!" : "Registration successful!")
            // Here you would typically save the user's token and redirect to a dashboard
        } catch (error) {
            console.log(error)
            setMessage(error.response?.data?.message ||"An error occurred")
        }
    }

    return (
        <div className={Styles.ancestorContainer}>
            <div className={Styles.headerContainer}>
                <h1>{isLogin ? "Login" : "Register"}</h1>
            </div>
            <form onSubmit={handleSubmit} className={Styles.authContainer}>
                {!isLogin && (
                    <input
                        className={Styles.authInput}
                        type='email'
                        placeholder='Email'
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                )}
                <input
                    className={Styles.authInput}
                    type='text'
                    placeholder='Username'
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    required
                />
                <input
                    className={Styles.authInput}
                    type='password'
                    placeholder='Password'
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
                <button type="submit" className={Styles.addButton}>
                    {isLogin ? "Login" : "Register"}
                </button>
            </form>
            <p>{message}</p>
            <button onClick={() => setIsLogin(!isLogin)} className={Styles.addButton}>
                {isLogin ? "Need to register?" : "Already have an account?"}
            </button>
        </div>
    )
}
