import { useEffect, useState } from 'react'
import Styles from './TODO.module.css'
import axios from 'axios'

export function AUTH(props) {

    const [newUser, setNewUSer] = useState('')
    const [loginUser, setLoginUser] = useState([]);
    const [loading, setLoading] = useState(true);

    
    const register = () => {
        const options = {
            method: "POST",
            url: `${process.env.REACT_APP_SERVER_URL}/register`,
            headers: {
                accept: "application/json"
            },
            data: {
                email: newUser,
                username:newUser,
                password:newUser
            }
        }
        axios
        .request(options)
        .then(response => {
            console.log(response.data);
            setLoginUser(prevData => [...prevData, response.data])
        })
        .catch(error => {
            console.log(error)
        })
    }

    const login = () => {
        const options = {
            method: "POST",
            url: `${process.env.REACT_APP_SERVER_URL}/login`,
            headers: {
                accept: "application/json"
            },
            data: {
                username:newUser,
                password:newUser
            }
        }
        axios
        .request(options)
        .then(response => {
            console.log(response.data);
            setLoginUser(prevData => [...prevData, response.data])
        })
        .catch(error => {
            console.log(error)
        })
    }

    
    return (
        <div className={Styles.ancestorContainer}>
            <div className={Styles.headerContainer}>
                <h1>
                    Blogs
                </h1>
                <span>
                    <input
                        className={Styles.todoInput}
                        type='text'
                        name='New Blog'
                        value={newUser}
                        onChange={(event) => {
                            setNewUSer(event.target.value)
                        }}
                    />
                    <button
                        id='addButton'
                        name='add'
                        className={Styles.addButton}
                        onClick={() => {
                            addBlog()
                            setNewUSer('')
                        }}
                    >
                        + New Blog
                    </button>
                </span>
            </div>
            <div id='blogContainer' className={Styles.todoContainer}>
                {loading ? (
                    <p style={{ color: 'white' }}>Loading...</p>
                ) : (
                    loginUser?.length > 0 ? (
                        loginUser?.map((entry, index) => (
                            <div key={entry._id} className={Styles.todo}>
                                <span className={Styles.infoContainer}>
                                    <input
                                        type='checkbox'
                                        checked={entry.done}
                                        onChange={() => {
                                            updateBlog(entry._id);
                                        }}
                                    />
                                    {entry.title}
                                </span>
                                <span
                                    style={{ cursor: 'pointer' }}
                                    onClick={() => {
                                        deleteBlog(entry._id);
                                    }}
                                >
                                    Delete
                                </span>
                            </div>
                        ))
                    ) : (
                        <p className={Styles.noTodoMessage}>No tasks available. Please add a new task.</p>
                    )
                )}
            </div>
        </div>
    )
}
